import React, { Component } from "react";
import ResultList from "./ResultList";
import SearchForm from "./SearchForm";
import API from "../utils/API";

class SearchResultContainer extends Component {
    state = {
        search: "",
        results: [],
        filter: []
    }

    componentDidMount() {
        this.ApiCall();
    }

    ApiCall = () => {
        API.search()
            .then(res => this.setState({
                results: res.data.results,
                filter: res.data.results
            })
            )
            .catch(err => console.log(err));
    };

    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        switch (name) {
            case "first":
                this.setState({
                    search: value,
                    filter: this.state.results.filter(result => result.name.first.toLowerCase().indexOf(value) != -1)
                });
                break;
            case "last":

                this.setState({
                    search: value,
                    filter: this.state.results.filter(result => result.name.last.toLowerCase().indexOf(value) != -1)
                });
                break;
        }
        console.log(name);



    };

    handleFormSubmit = event => {
        event.preventDefault();
        this.ApiCall();
    };
    handleSort = event => {
        let sort="asc";
        function compare(a, b) {
            switch (sort) {
                case "asc":
                    if (a.dob.age > b.dob.age) return 1;
                    if (b.dob.age > a.dob.age) return -1;
                case "desc":

                    if (a.dob.age > b.dob.age) return -1;
                    if (b.dob.age > a.dob.age) return 1;
            }


        }
        this.setState({
            filter: this.state.results.sort(compare)
        });
        console.log(compare);
    }

    render() {
        return (
            <div>
                <SearchForm
                    //search = {this.state.search}
                    //handleFormSubmit = {this.handleFormSubmit}
                    handleInputChange={this.handleInputChange}

                />
                <ResultList
                    results={this.state.filter}
                    handleSort={this.handleSort}
                />
            </div>
        );
    }
}
export default SearchResultContainer;
